// import TopNav from './TopNav';
import uuid from 'uuid'
import {getToday} from './commonCommands'

class App extends Component {
    state = {
        goals:{
            dailyGoals : [

            ],
            otherGoalsCategories:[
                {
                    category: 'Programming',
