import React, { Component } from 'react';
import {getYeseterday} from '../commonCommands'

class DailyGoals extends Component {
    state = {
        renderAmount: 0
    }
    heading = React.createRef();
    componentDidMount() {
      let len = this.heading.current.offsetWidth
      this.updateState(len);
    }
    updateState = (len)=> {
        // size of header is 200px checkmark is 80 px
        len = Math.floor((len - 200)/80)
        len > 8 ? len = 8 : len = len
        let state = this.state
        state.renderAmount = len
        this.setState({
            state: state
        })
        console.log(this.state.renderAmount)
    }
    listElement = (i) =>{
        return(
            <li>{this.getWeekDay(i)}<br/>{this.getMonthDay(i)}</li>
        )
    }
    positivedateRenders = (len) => {
        let list =[
        ];
        for(let i = 1; i <= len; i++){
            list.push(this.listElement(i))
        }
        return(
            [list]
        )
    }
    negativedateRenders = (len) => {
        let list =[
        ];
        if(!((this.state.renderAmount % 2) === 0)){
            len--;
        }
        for(let i = -1; i >= len; i--){
            list.push(this.listElement(i))
        }
        return(
            [list]
        )
    }
    onClick = (e) => {
        this.props.deleteGoal(e.target.title, 'daily')
    }
    updateCheckMark = (e) => {
        console.log('THE ID ',e.target.id)
        this.props.updateCheckMark(e.target.id)
    }
    
    render() {
        const{dailyGoals, deleteGoal, updateCheckMark} = this.props;
        const displayCheckBoxes = (goal) =>{
                return(
                    <React.Fragment>
                    <div className="onedailygoalcheckmark">
                    <ul>
                    <li>
                        <label className="checkbox">
                            <input type="checkbox" checked={goal.weeklyChecked[0]} readOnly={true}/>
                            <span className="checkmark"></span>
                        </label>
                    </li>
                    <li>
                        <label className="checkbox">
                            <input type="checkbox" checked={goal.weeklyChecked[1]} readOnly={true}/>
                            <span className="checkmark"></span>
                        </label>
                    </li>
                    <li>
                        <label className="checkbox">
                            <input type="checkbox" checked={goal.weeklyChecked[2]} readOnly={true}/>
                            <span className="checkmark"></span>
                        </label>
                    </li>
                    <li>
                        <label className="checkbox">
                            <input type="checkbox" checked={goal.weeklyChecked[3]} readOnly={true}/>
                            <span className="checkmark"></span>
                        </label>
                    </li>
                    <li>
                        <label className="checkbox checkboxmain">
                            <input type="checkbox" checked={goal.weeklyChecked[4]} readOnly={true} onClick={() => {this.props.updateCheckMark(goal.id)}} />
                            <span className="checkmark"></span>
                        </label>
                    </li>
                    <li>
                        <label className="checkbox">
                            <input type="checkbox" checked={goal.weeklyChecked[5]} readOnly={true}/>
                            <span className="checkmark"></span>
                        </label>
                    </li>
                    <li>
                        <label className="checkbox">
                            <input type="checkbox" checked={goal.weeklyChecked[6]} readOnly={true}/>
                            <span className="checkmark"></span>
                        </label>
                    </li>
                    <li className="close-container"onClick={() => {this.props.deleteGoal(goal.id, 'daily')}}>
                        <div className="leftright"></div>
                        <div className="rightleft"></div>
                        <label className="close">close</label>
                    </li>
                    </ul>
                    </div>
                    </React.Fragment>
                )
        }
        const displayDailyGoals = dailyGoals.map(goal => {
            if(Date.parse(goal.endDate) < Date.parse(getYeseterday())){
                {this.props.deleteGoal(goal.id, 'daily')}
            }
            return(
                <div className="onedailygoal" key={goal.id} ref={this.heading}>
                    <div className="onedailygoalheading" >
                        <h4>{goal.title}</h4>{goal.snippit}
                    </div>
                    {displayCheckBoxes(goal)}
                </div>
            )
        })

        return (
            <div>
                { displayDailyGoals }
            </div>
        )
    }
}
export default DailyGoals
