import React, { Component } from 'react'

class Overlay extends Component {
    state = {
        title: null,
        snippit: null,
        // '2019, 7, 1 00:00',
        endDate: null,
        type: 'daily',
        category: null
    }
    onChange = (e) => {
        this.setState({
            [e.target.id]: e.target.value
        })
    }
    typeOnChange = (e) => {
        this.setState({
            type: e.target.value
        })
    }
    checkDisabled = () => {
        console.log(this.state.type)
        console.log(this.state.category)
        if(this.state.type == 'daily' || this.state.category == 'newCategory'){
            return true;
        }
        return false;
    }
    render() {
        const{otherGoalCategories , closeGoalOverlay} = this.props;
        const categories = otherGoalCategories.map(category => {
            return(
                <option value={category.category}>{category.category}</option>
            )
        })
        return (
            <div className="creategoaloverlay" id="creategoaloverlay">
              <div className="creategoal">
                  <h1>Create Goal</h1>
                  <div className="goalallinput">
                      <ul>
                            <li >Goal:<br/><input className="goalinput goal" id="title" type="text" name="goal" onChange={this.onChange}/></li>
                            <li >Short Description:<br/> <input className="goalinput sDes" id="snippit" type="text" name="sDes" onChange={this.onChange}/></li>
                            <li >Ends:<br/> <input type="date" className="goalinput dDate" id="endDate" name="dDate" onChange={this.onChange}/></li>
                            <li>Type:
                            <select name="type" className="type" onChange={this.typeOnChange}>
                                <option value="daily">Daily</option>
                                <option value="default">Default</option>
                            </select>
                            </li>
                            Category:
                            <select name="type" className="type" id='category' onChange={this.onChange} disabled={this.state.type == 'daily'}>
                                <option value="newCategory">New Category</option>
                                {categories}
                            </select>
                            <li >New Category:<br/> <input className="goalinput category" id="category" type="text" name="category" onChange={this.onChange} disabled={this.checkDisabled}/></li>
                      </ul>
                  </div>
                    <div className="submitarea">
                        <input id="cancelbutton" className="button" type="submit" value="Cancel" onClick={closeGoalOverlay}/>
                        <div className="submitbutton">
                            <input className="button submit" type="submit" value="Submit"/>
                        </div>
                    </div>
              </div>
          </div>
        )
    }
}

export default Overlay
