    getWeekDay = (offset) => {
        let weekdays = new Array(
            "Sun", "Mon", "Tue", "Wed", "Thu", "Fri", "Sat"
        );
        let today = new Date;
        return weekdays[new Date(today.getFullYear(), today.getMonth(),  today.getDate() + offset).getDay()]
    }
    getMonthDay = (offset) => {
        let today = new Date(today.getFullYear(), today.getMonth(),  today.getDate() + offset);
        return today.getDate
    }
    render() {
        return (
            <div className="dailyheading">
                    <div className="dailyheadingheading">
                        <h1>Daily Goals</h1>
                    </div>
                    <ul>
                        {/* {this.dateRenders()} */}
                        <li>{this.getWeekDay(-4)}<br/>{this.getWeekDay(-4)}</li>
