import React, { Component } from 'react'

class Overlay extends Component {
    state = {
        title: null,
        snippit: null,
        startDate: null,
        // '2019, 7, 1 00:00',
        endDate: null,
        type: null
    }
    typeOnChange = (e) => {
        console.log('yee')
        this.setState({
            type: e.target.value
        })
        return(
            <li>Category:<br/> <input className="goalinput category" type="text" name="category" disabled={true}/></li>
        )
    }
    render() {
        // const displayChecks = () =>{
            
        // }
        return (
            <div className="creategoaloverlay" id="creategoaloverlay">
              <div className="creategoal">
                  <h1>Create Goal</h1>
                  <div className="goalallinput">
                      <ul>
                            <li id="goal">Goal:<br/><input className="goalinput goal" type="text" name="goal"/></li>
                            <li>Short Description:<br/> <input className="goalinput sDes" type="text" name="sDes"/></li>
                            <li>Type:<br/>
                            <select name="type" className="type" onChange={this.typeOnChange}>
                                <option value="daily">Daily</option>
                                <option value="default">Default</option>
                            </select>
                            <li>Ends:<br/> <input className="goalinput dDate" type="text" name="dDate"/></li>
                            </li>
                            <li>Category:<br/> <input className="goalinput category" type="text" name="category"/></li>
                      </ul>
                  </div>
                    <div className="submitarea">
                        <input id="cancelbutton" className="button" type="submit" value="Cancel" onClick={this.props.closeGoalOverlay}/>
                        <div className="submitbutton">
                            <input className="button submit" type="submit" value="Submit"/>
                        </div>
                    </div>
              </div>
          </div>
        )
    }
}

export default Overlay
