import ReactDOM from 'react-dom';
import React, { Component } from 'react';
import DailyTodos from './DailyTodos/DailyTodos'
import './App.css';

class App extends React.Component{
  state = {
    allTodos:{
      dailyTodos:[
        {
          id: 1,
          title: 'Programme',
          daysChecked: 20,
          onDay: 25,
          totaldays: 30,
          thisWeek:[true, false, true, true, false, null, null]
        },
        {
          id: 2,
          title: 'Study React',
          daysChecked: 3,
          onDay: 7,
          totaldays: 30,
          thisWeek:[true, false, true, true, false, null, null]
        },
        {
          id: 3,
          title: 'Do Stuff',
          daysChecked: 20,
          onDay: 25,
          totaldays: 30,
          thisWeek:[false, false, false, false, false, null, null]
        }
      ],
      otherTodosCategory:[
        {
          category: 'Programming',
          otherTodos:[
            {
              id: 1,
              title: 'Become React Pro',
              startDate: new Date("3/7/2019"),
              endDate: new Date("3/8/2019"),
              percentComplete: 20
            },
            {
              id: 2,
              title: 'Become React Pro 2',
              startDate: new Date("3/7/2019"),
              endDate: new Date("3/8/2019"),
              percentComplete: 40
            }
          ]
        },
        {
          category: 'Reading',
          otherTodos:[
            {
              id: 1,
              title: 'Read a book',
              startDate: new Date("1/7/2019"),
              endDate: new Date("30/7/2019"),
              percentComplete: 0
            },
            {
              id: 2,
              title: 'Read 30 books',
              startDate: new Date("1/1/2019"),
              endDate: new Date("1/1/2020"),
              percentComplete: 40
            }
          ]
        }
      ]
    }
  }

  render(){
    return (
      <div className='root'>
        {displayTodos = this.state.allTodos.dailyTodos.map(todo =>{
        return(
        <div className="onedailygoal" >
            <div className="onedailygoalheading">
                <h4>Programming</h4>Write Code
                {console.log(todo.title)}
            </div>
        </div>
        )
    })}
        {/* <DailyTodos dailyTodos={this.state.allTodos.dailyTodos}/> */}
        {/* {console.log(this.state.allTodos.dailyTodos)} */}
      </div>
    )
  }
}

export default App;

const createGoalBtn = document.getElementById('button');
const goalCreatePopup = document.getElementById('creategoaloverlay');
const cancelBtn = document.getElementById('cancelbutton');

createGoalBtn.addEventListener('click', ()=>{
    goalCreatePopup.style.display = "block";
}, false)

cancelBtn.addEventListener('click', ()=>{
    goalCreatePopup.style.display = "none";
}, false)

const navSlide = () => {
    const navdropdown = document.querySelector('.navdropdown');
    const nav = document.querySelector('.sidenav');
    const navlinks = document.querySelectorAll('.navlinks a')

    navdropdown.addEventListener('click', ()=>{
        nav.classList.toggle('nav-active');
        navlinks.forEach((link, index)=>{
            if(link.style.animation){
                link.style.animation = '';
            }
            else{
                link.style.animation = `navLinkFade 0.5s ease forwards ${(index / 7) + .15}s`;
            }
        });

        navdropdown.classList.toggle('toggle');
    });
}
navSlide();
