import React, {Component} from 'react';
import './App.css';
import DailyGoals from './DailyGoals'

class App extends Component {
    state = {
        goals:{
            dailyGoals : [
                {
                    id: 1,
                    title: 'Programme',
                    snippit: 'Write Code',
                    totalDays: 30,
                    daysChecked: 20,
                    weeklyChecked: [true, false, true, true, false, null, null]
                },
                {
                    id: 2,
                    title: 'Learn React',
                    snippit: 'Write Code',
                    totalDays: 30,
                    daysChecked: 20,
                    weeklyChecked: [true, false, true, true, false, null, null]
                },
                {
                    id: 3,
                    title: 'Be Cool',
                    snippit: 'Yea',
                    totalDays: 30,
                    daysChecked: 20,
                    weeklyChecked: [true, false, true, true, false, null, null]
                }
            ]
        }
    }
    render() {
        return (
            <div className="onedailygoal">
                <DailyGoals dailyGoals={this.state.goals.dailyGoals} />
            </div>
          );
    }
}
