import React, {Component} from 'react';
import './App.css';

class App extends Component {
    state = {
        dailyGoals : [
            {
                id: 1,
                title: 'Programme',
                snippit: 'Write Code',
                totalDays: 30,
                daysChecked: 20,
                weeklyChecked: [true, false, true, true, false, null, null]
            },
            {
                id: 2,
                title: 'Learn React',
                snippit: 'Write Code',
                totalDays: 30,
                daysChecked: 20,
                weeklyChecked: [true, false, true, true, false, null, null]
            },
            {
                id: 3,
                title: 'Be Cool',
                snippit: 'Yea',
                totalDays: 30,
                daysChecked: 20,
                weeklyChecked: [true, false, true, true, false, null, null]
            }
        ]
    }
    render() {
        return (
            <div className="onedailygoal">
                {this.state.dailyGoals.map(props => {
                    return(
                        <div className="onedailygoal">
                            <div className="onedailygoalheading">
                                <h4>{props.title}</h4>Write Code
                            </div>
                            <div className="onedailygoalcheckmark">
                        <ul>
                            <li>
                                <label className="checkbox">
                                    <input type="checkbox"/>
                                    <span class="checkmark"></span>
                                </label>
                            </li>
                            <li>
                                <label className="checkbox">
                                    <input type="checkbox"/>
                                    <span class="checkmark"></span>
                                </label>
                            </li>
                            <li>
                                <label className="checkbox">
                                    <input type="checkbox"/>
                                    <span class="checkmark"></span>
                                </label>
                            </li>
                            <li>
                                <label className="checkbox">
                                    <input type="checkbox"/>
                                    <span class="checkmark"></span>
                                </label>
                            </li>
                            <li>
                                <label className="checkbox">
                                    <input type="checkbox"/>
                                    <span class="checkmark"></span>
                                </label>
                            </li>
                            <li>
                                <label className="checkbox">
                                    <input type="checkbox"/>
                                    <span class="checkmark"></span>
                                </label>
                            </li>
                            <li>
                                <label className="checkbox">
                                    <input type="checkbox"/>
                                    <span class="checkmark"></span>
                                </label>
                            </li>
                            <li>
                                <label className="checkbox">
                                    <input type="checkbox"/>
                                    <span class="checkmark"></span>
                                </label>
                            </li>
                        </ul>
                    </div>
                        </div>
                    )
                })
                }
            </div>
          );
    }
}

export default App;

const createGoalBtn = document.getElementById('button');
const goalCreatePopup = document.getElementById('creategoaloverlay');
const cancelBtn = document.getElementById('cancelbutton');

createGoalBtn.addEventListener('click', ()=>{
    goalCreatePopup.style.display = "block";
}, false)

cancelBtn.addEventListener('click', ()=>{
    goalCreatePopup.style.display = "none";
}, false)

const navSlide = () => {
    const navdropdown = document.querySelector('.navdropdown');
    const nav = document.querySelector('.sidenav');
    const navlinks = document.querySelectorAll('.navlinks a')

    navdropdown.addEventListener('click', ()=>{
        nav.classList.toggle('nav-active');
        navlinks.forEach((link, index)=>{
            if(link.style.animation){
                link.style.animation = '';
            }
            else{
                link.style.animation = `navLinkFade 0.5s ease forwards ${(index / 7) + .15}s`;
            }
        });

        navdropdown.classList.toggle('toggle');
    });
}
navSlide();
