import React, { Component } from 'react';
import './App.css';

class TypeSelector extends Component {
    state = {
        dailyLength: 0,
        otherLength: 0,
        completedLength: 0
    }
    render() {
        const{goals} = this.props.goals;

        const getLengths = () =>{
            let state = this.state;
            let length
            let lengthTemp
            // daily
            state.dailyLength = (this.props.goals.dailyGoals).length

            // otherGoals
            this.props.goals.otherGoalsCategories.map(catagories => {
                lengthTemp = catagories.otherGoals.length
                state[catagories.catagory] = lengthTemp
                length += lengthTemp
                lengthTemp = 0
            })
            state.otherLength = length
            length = 0
            lengthTemp = 0

            //completed
            // daily
            length = (this.props.goals.dailyGoals).length

            // otherGoals
            this.props.goals.otherGoalsCategories.map(catagories => {
                lengthTemp = catagories.otherGoals.length
                state[catagories.catagory] = lengthTemp
                length += lengthTemp
                lengthTemp = 0
            })
            state.completedLength = length
            return(
                <div className="goaltypeselector">
                    <h2>Sort Goals</h2>
                    <div className="sort">
                        <ul>
                            <li><img src="../src/Images/pressed.png" alt=""/><span>Show Current {state.dailyLength + state.otherLength}</span></li> 
                            <li><img src="../src/Images/pressed.png" alt=""/><span>Show Completed {state.otherLength}</span></li>
                        </ul>
                    </div>
                    <div className="goaltype">
                        <h3>TimeFrame</h3>
                        <ul>
                            <li><img src="../src/Images/pressed.png" alt=""/><span>Daily Goals {state.dailyLength}</span></li> 
                            <li><img src="../src/Images/pressed.png" alt=""/><span>Other Goals { this.state.otherLength}</span></li>
                        </ul>
                    </div>
                    <div className="catagories">
                        <h3>Catagories</h3>
                        <ul>
                            <li><img src="../src/Images/pressed.png" alt=""/><span>Programming</span></li> 
                            <li><img src="../src/Images/pressed.png" alt=""/><span>Knowledge</span></li>
                            <li><img src="../src/Images/pressed.png" alt=""/><span>Workout</span></li>
                        </ul>
                    </div>
                </div>
            )
        }
        const renderSelector = () =>{
            return(
                <div className="goaltypeselector">
                    <h2>Sort Goals</h2>
                    <div className="sort">
                        <ul>
                            <li><img src="../src/Images/pressed.png" alt=""/><span>Show Current {this.state.dailyLength + this.state.otherLength}</span></li> 
                            <li><img src="../src/Images/pressed.png" alt=""/><span>Show Completed {this.state.otherLength}</span></li>
                        </ul>
                    </div>
                    <div className="goaltype">
                        <h3>TimeFrame</h3>
                        <ul>
                            <li><img src="../src/Images/pressed.png" alt=""/><span>Daily Goals {this.state.dailyLength}</span></li> 
                            <li><img src="../src/Images/pressed.png" alt=""/><span>Other Goals { this.state.otherLength}</span></li>
                        </ul>
                    </div>
                    <div className="catagories">
                        <h3>Catagories</h3>
                        <ul>
                            <li><img src="../src/Images/pressed.png" alt=""/><span>Programming</span></li> 
                            <li><img src="../src/Images/pressed.png" alt=""/><span>Knowledge</span></li>
                            <li><img src="../src/Images/pressed.png" alt=""/><span>Workout</span></li>
                        </ul>
                    </div>
                </div>
            )
        }
        return (
            <React.Fragment>
            {getLengths()}
            </React.Fragment>
        )
    }
}
export default TypeSelector
