import React, { Component } from 'react';

class DailyGoalHeading extends Component {
    state = {
        renderAmount: 0
    }
    heading = React.createRef();
    componentDidMount() {
      let len = this.heading.current.offsetWidth
      this.dateRenders(len);
    }
    dateRenders = (len)=> {
        len = len - 200
        len = len/103
        console.log(len)
    }
    getWeekDay = (offset) => {
        let weekdays = new Array(
            "Sun", "Mon", "Tue", "Wed", "Thu", "Fri", "Sat"
        );
        let today = new Date;
        return weekdays[new Date(today.getFullYear(), today.getMonth(),  today.getDate() + offset).getDay()]
    }
    getMonthDay = (offset) => {
        let today = new Date;
        today = new Date(today.getFullYear(), today.getMonth(),  today.getDate() + offset);
        return today.getDate()
    }
    render() {
        return (
            <div className="dailyheading" ref={this.heading}>
                    <div className="dailyheadingheading">
                        <h1>Daily Goals</h1>
                    </div>
                    <ul>
                        {/* {this.dateRenders()} */}
                        <li>{this.getWeekDay(-4)}<br/>{this.getMonthDay(-4)}</li>
                        <li>{this.getWeekDay(-3)}<br/>{this.getMonthDay(-3)}</li>
                        <li>{this.getWeekDay(-2)}<br/>{this.getMonthDay(-2)}</li>
                        <li>{this.getWeekDay(-1)}<br/>{this.getMonthDay(-1)}</li>
                        <li>{this.getWeekDay(0)}<br/>{this.getMonthDay(0)}</li>
                        <li>{this.getWeekDay(1)}<br/>{this.getMonthDay(1)}</li>
                        <li>{this.getWeekDay(2)}<br/>{this.getMonthDay(2)}</li>
